mapper.select:
SELECT MOVIE_NO, TO_CHAR(MOVIE_TIME, 'HH24:MI') MOVIE_TIME, MOVIE_THEATER, TO_CHAR(MOVIE_TIME, 'YYYYMMDD HH24:MI') CHECK_TIME
FROM MOVIE_SCHEDULE
WHERE TO_CHAR(MOVIE_TIME, 'YYYYMMDD') = ${movieTime}

AND MOVIE_TIME > SYSDATE 

AND MOVIE_NO = ${movieNo}
ORDER BY MOVIE_TIME, MOVIE_THEATER

SELECT MOVIE_NO, MOVIE_TITLE, POSTER, RUNNING_TIME, RATING, RELEASE_DATE, MOVIE_PRICE, GENRE
FROM MOVIE
WHERE MOVIE_NO = ${movieNo}

SELECT SEAT_NO, MOVIE_THEATER
FROM SEAT
WHERE MOVIE_THEATER = ${movieTheater}
